import React, { useState, useEffect } from "react";
import { Route, Link, Routes } from "react-router-dom";
import SelectedItems from "./SelectedItems";
import AddEditItems from "./AddEditItems";
import "./Admin.css";
import { useRef } from "react";
import { io } from "socket.io-client";
import LoginForm from "./LoginForm";

const ADMIN_USERNAME = "dubz";
const ADMIN_PASSWORD = "130478";

export default function Admin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState(null);
  const [selectedItems, setSelectedItems] = useState([]);
  const [itemOptions, setItemOptions] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (username === ADMIN_USERNAME && password === ADMIN_PASSWORD) {
      setLoggedIn(true);
      setError(null);
    } else {
      setError("Invalid username or password");
    }
  };

  useEffect(() => {
    if (loggedIn) {
      const fetchData = async () => {
        setLoading(true);

        const [itemOptionsResponse, adminResponse] = await Promise.all([
          fetch("http://localhost:5000/food_options"),
          fetch("http://localhost:5000/admin"),
        ]);

        const itemOptionsData = await itemOptionsResponse.json();
        const adminData = await adminResponse.json();

        setItemOptions(itemOptionsData);
        setSelectedItems(adminData);
        setLoading(false);
      };

      fetchData();
    }
  }, [loggedIn]);

  const socketRef = useRef();

  useEffect(() => {
    if (loggedIn) {
      socketRef.current = io("http://localhost:5000");

      socketRef.current.on("connect", () => {
        console.log("Connected to server");
        socketRef.current.emit("admin_connected");
      });

      socketRef.current.on("selected_items_update", (data) => {
        console.log("Received data:", data);
        if (data.length > 0) {
          const updatedData = data.map((newItem) => {
            const existingItem = selectedItems.find((item) => item.id === newItem.id);
            return existingItem ? { ...existingItem, count: newItem.count } : newItem;
          });
          setSelectedItems(updatedData);
        }
      });

      return () => {
        socketRef.current.disconnect();
      };
    }
  }, [loggedIn, selectedItems]);

  const getItemName = (id) => {
    const item = itemOptions.find((option) => option.id === parseInt(id));
    return item ? item.name : "Unknown";
  };

  return (
    <div>
      {!loggedIn ? (
        <div className="container">
          <LoginForm
            username={username}
            password={password}
            setUsername={setUsername}
            setPassword={setPassword}
            handleSubmit={handleSubmit}
            error={error}
          />
        </div>
      ) : (
        <div className="admin-container">
          <div className="sidebar">
            <ul>
              <li>
                <Link to="/admin/selected-items">Selected Items</Link>
              </li>
              <li>
                <Link to="/admin/add-edit-items">Add/Edit Items</Link>
              </li>
            </ul>
          </div>
          <div className="admin-content">
            <Routes>
              <Route
                path="/admin/selected-items"
                element={<SelectedItems selectedItems={selectedItems} getItemName={getItemName} />}
              />
              <Route
                path="/admin/add-edit-items"
                element={<AddEditItems itemOptions={itemOptions} setItemOptions={setItemOptions} />}
              />
            </Routes>
          </div>
        </div>
      )}
    </div>
  );
}
