import React, { useState } from "react";
import axios from "axios";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  VStack,
  useToast,
} from "@chakra-ui/react";
import ImageDropZone from "./ImageDropZone";
const AddEditItems = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);

  const onFileChange = (e) => setImage(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description || !image) {
      alert("Please fill all the fields and select an image.");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/add_item", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.status === "success") {
        alert("Item added successfully!");
        setName("");
        setDescription("");
        setImage(null);
      } else {
        alert("Error: " + response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error: Unable to add the item.");
    }
  };

  return (
    <Box maxWidth="600px" mx="auto" mt="10">
      <VStack spacing={6}>
        <FormControl>
          <FormLabel>Item Name</FormLabel>
          <Input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter item name"
          />
        </FormControl>
        <FormControl>
          <FormLabel>Item Description</FormLabel>
          <Textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter item description"
          />
        </FormControl>
        <Box>
          <FormLabel>Item Image</FormLabel>
          <ImageDropZone onImageChange={setImage} />
        </Box>
        <Button onClick={handleSubmit} colorScheme="blue">
          Submit
        </Button>
      </VStack>
    </Box>
  );
};

export default AddEditItems;
