import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import ImageItem from "./ImageItem";
import ImageDropZone from "./ImageDropZone";

const AddEditItems = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setSelectedImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle the form submission here
    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", selectedImage);
  
    const response = await fetch("http://localhost:5000/api/add_item", {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data",
      },
      body: formData,
    });
  
    if (response.ok) {
      const data = await response.json();
      console.log("Item added successfully:", data);
      // Reset the form fields and selected image
      setName("");
      setDescription("");
      setSelectedImage(null);
    } else {
      console.error("Error adding item:", await response.json());
    }
  };
  

  return (
    <div className="add-edit-items-container">
      <h1>Add/Edit Items</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} required />

        <label htmlFor="description">Description:</label>
        <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)} required />

        <label htmlFor="image">Image:</label>
        <ImageDropZone onImageChange={onImageChange} />

        <button type="submit">Submit</button>
      </form>

      {selectedImage && <img src={selectedImage} alt="Selected" />}
    </div>
  );
}

export default AddEditItems;
