import React, { useState, useEffect } from "react";
import axios from "axios";

const AddEditItems = () => {
  const [itemId, setItemId] = useState(null);
  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [previewImageUrl, setPreviewImageUrl] = useState("");

  useEffect(() => {
    const fetchItems = async () => {
      const response = await axios.get("http://localhost:5000/items");
      setItems(response.data.items);
    };

    fetchItems();
  }, []);

  const onFileChange = (e) => {
    setImage(e.target.files[0]);
    setPreviewImageUrl(URL.createObjectURL(e.target.files[0]));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description || !image) {
      alert("Please fill all the fields and select an image.");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", image);

    try {
      const response = await axios.post(
        `http://localhost:5000/${itemId ? "update_item" : "add_item"}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.status === "success") {
        alert(itemId ? "Item updated successfully!" : "Item added successfully!");
        setName("");
        setDescription("");
        setImage(null);
        setPreviewImageUrl("");
        setItemId(null);
      } else {
        alert("Error: " + response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error: Unable to add the item.");
    }
  };

  const selectItem = (item) => {
    setItemId(item.id);
    setName(item.name);
    setDescription(item.description);
    setPreviewImageUrl(item.image_url);
  };

  return (
    <div className="container mx-auto px-4">
      <h1 className="text-xl font-bold mb-4">Add/Edit Items</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="flex flex-col">
          <label htmlFor="name" className="font-medium">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="border border-gray-300 p-2 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label htmlFor="description" className="font-medium">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="border border-gray-300 p-2 rounded"
          />
        </div>
        <div className="flex flex-col">
          <label htmlFor="image" className="font-medium">Image:</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={onFileChange}
            className="border border-gray-300 p-2 rounded"
          />
        </div>
        {previewImageUrl && (
          <div className="mt-4">
            <img
              src={previewImageUrl}
              alt="Preview"
              className="w-32 h-auto border border-gray-300 p-1 rounded"
            />
          </div>
        )}
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          {itemId ? "Update" : "Submit"}
        </button>
      </form>
      <div className="mt-8">
        <h2 className="text-xl font-bold mb-4">Items:</h2>
        <ul className="space-y-2">
          {items.map((item) => (
            <li
              key={item.id}
              className="flex items-center space-x-4 cursor-pointer hover:bg-gray-100 p-2 rounded"
              onClick={() => selectItem(item)}
            >
              <img
                src={item.image_url}
                alt={item.name}
                className="w-16 h-auto border border-gray-300 p-1 rounded"
              />
              <div className="flex flex-col">
                <span className="font-bold">{item.name}</span>
                <span className="text-gray-600">{item.description}</span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AddEditItems;
