import React, { useState, useEffect } from "react";
import "./App.css";

export default function App() {
  const [pizzaOptions, setPizzaOptions] = useState([]);
  const [selectedPizzas, setSelectedPizzas] = useState(new Set());

  useEffect(() => {
    fetch("/pizza_options")
      .then((res) => res.json())
      .then((data) => setPizzaOptions(data));
  }, []);

  const handleChange = (event) => {
    const { value } = event.target;
    if (selectedPizzas.has(value)) {
      selectedPizzas.delete(value);
    } else {
      selectedPizzas.add(value);
    }
    setSelectedPizzas(new Set(selectedPizzas));
  };

  const handleSubmit = () => {
    fetch("/selected_pizzas", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(Array.from(selectedPizzas)),
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  return (
    <div className="App">
      <div className="pizza-container">
        {pizzaOptions.map((pizza, i) => (
          <div key={i} className="pizza-box">
            <img src={pizza.image} alt={pizza.name} />
            <div>
              <label>
                <input
                  type="checkbox"
                  value={pizza.id}
                  onChange={handleChange}
                />
                {pizza.name}
              </label>
            </div>
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}
