import React, { useState, useEffect } from "react";

const API_BASE_URL = "http://localhost:5000";

function AddEditItems() {
  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [editItemId, setEditItemId] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    const response = await fetch(`${API_BASE_URL}/food_options`);
    const data = await response.json();
    setItems(data);
  };

  const addItem = async () => {
    const newItem = { name, description, image };
    await fetch(`${API_BASE_URL}/add_item`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newItem),
    });
    fetchItems();
    clearForm();
  };

  const editItem = async () => {
    const updatedItem = { id: editItemId, name, description, image };
    await fetch(`${API_BASE_URL}/edit_item`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedItem),
    });
    fetchItems();
    clearForm();
  };

  const removeItem = async (id) => {
    await fetch(`${API_BASE_URL}/remove_item`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id }),
    });
    fetchItems();
  };

  const startEdit = (item) => {
    setName(item.name);
    setDescription(item.description);
    setImage(item.image);
    setEditItemId(item.id);
  };

  const clearForm = () => {
    setName("");
    setDescription("");
    setImage("");
    setEditItemId(null);
  };

  return (
    <div>
      <h2>Add/Edit Items</h2>
      <div>
        <label>Name:</label>
        <input value={name} onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label>Description:</label>
        <input value={description} onChange={(e) => setDescription(e.target.value)} />
      </div>
      <div>
        <label>Image URL:</label>
        <input value={image} onChange={(e) => setImage(e.target.value)} />
      </div>
      {editItemId ? (
        <button onClick={editItem}>Update Item</button>
      ) : (
        <button onClick={addItem}>Add Item</button>
      )}
      <button onClick={clearForm}>Clear</button>
      <h3>Items</h3>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            <div>{item.name}</div>
            <div>{item.description}</div>
            <img src={item.image} alt={item.name} width="100" />
            <button onClick={() => startEdit(item)}>Edit</button>
            <button onClick={() => removeItem(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AddEditItems;
