import React, { useState } from "react";

function AddEditItems({ itemOptions, setItemOptions }) {
  const [newItemName, setNewItemName] = useState("");
  const [newItemImage, setNewItemImage] = useState("");
  const [newItemDescription, setNewItemDescription] = useState("");

  const handleAddItem = async (event) => {
    event.preventDefault();

    const newItem = {
      name: newItemName,
      image: newItemImage,
      description: newItemDescription,
    };

    const response = await fetch("http://localhost:5000/add_item", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newItem),
    });

    const data = await response.json();

    if (data.status === "success") {
      setNewItemName("");
      setNewItemImage("");
      setNewItemDescription("");
      setItemOptions([...itemOptions, data.item]);
    } else {
      console.error(data.message);
    }
  };

  return (
    <Box>
      <Heading as="h2" size="lg" mb={6}>
        Add New Item
      </Heading>
      <form onSubmit={handleAddItem}>
        <FormControl id="name" isRequired mb={4}>
          <FormLabel>Name:</FormLabel>
          <Input
            type="text"
            value={newItemName}
            onChange={(e) => setNewItemName(e.target.value)}
          />
        </FormControl>

        <FormControl id="image" isRequired mb={4}>
          <FormLabel>Image URL:</FormLabel>
          <Input
            type="text"
            value={newItemImage}
            onChange={(e) => setNewItemImage(e.target.value)}
          />
        </FormControl>

        <FormControl id="description" isRequired mb={4}>
          <FormLabel>Description:</FormLabel>
          <Textarea
            value={newItemDescription}
            onChange={(e) => setNewItemDescription(e.target.value)}
          />
        </FormControl>

        <Button type="submit" colorScheme="blue">
          Add Item
        </Button>
      </form>
    </Box>
  );
}
