import React, { useState } from "react";
import axios from "axios";

const AddEditItems = () => {
  const [tab, setTab] = useState("add");
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);

  const onFileChange = (e) => setImage(e.target.files[0]);
  const [editItemId, setEditItemId] = useState("");
  const [editItem, setEditItem] = useState(null);
  const [editName, setEditName] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [removeItemId, setRemoveItemId] = useState("");
  
  // Add these functions
  const handleFetchItem = async () => {
    // Fetch the item with the given ID and set the editItem, editName, and editDescription state variables.
  };
  
  const handleUpdateItem = async () => {
    // Update the item with the given ID using the editName and editDescription state variables.
  };
  
  const handleRemoveItem = async () => {
    // Remove the item with the given ID.
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description || !image) {
      alert("Please fill all the fields and select an image.");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/add_item", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.status === "success") {
        alert("Item added successfully!");
        setName("");
        setDescription("");
        setImage(null);
      } else {
        alert("Error: " + response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error: Unable to add the item.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-full md:w-3/4 lg:w-1/2">
        <div className="flex justify-between">
          <button
            className={`px-4 py-2 ${tab === "add" ? "bg-blue-600 text-white" : "bg-blue-200 text-blue-600"} rounded-md`}
            onClick={() => setTab("add")}
          >
            Add Item
          </button>
          <button
            className={`px-4 py-2 ${tab === "edit" ? "bg-blue-600 text-white" : "bg-blue-200 text-blue-600"} rounded-md`}
            onClick={() => setTab("edit")}
          >
            Edit Item
          </button>
          <button
            className={`px-4 py-2 ${tab === "remove" ? "bg-blue-600 text-white" : "bg-blue-200 text-blue-600"} rounded-md`}
            onClick={() => setTab("remove")}
          >
            Remove Item
          </button>
        </div>
        {tab === "add" && (
          <form onSubmit={handleSubmit} className="mt-4">
            <div>
              <label htmlFor="name" className="block mb-1">
                Name:
              </label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded-md"
              />
            </div>
            <div className="mt-4">
              <label htmlFor="description" className="block mb-1">
                Description:
              </label>
              <textarea
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded-md"
              />
            </div>
            <div className="mt-4">
              <label htmlFor="image" className="block mb-1">
Image:
</label>
<input
             type="file"
             id="image"
             accept="image/*"
             onChange={onFileChange}
             className="w-full p-2 border border-gray-300 rounded-md"
           />
</div>
<button type="submit" className="mt-4 bg-blue-600 text-white px-4 py-2 rounded-md">
Submit
</button>
</form>
)}
{tab === "edit" && (
<div className="mt-4">
// Edit Item
{tab === "edit" && (
  <div className="mt-4">
    <label htmlFor="editItemId" className="block mb-2">
      Item ID:
    </label>
    <input
      type="text"
      id="editItemId"
      value={editItemId}
      onChange={(e) => setEditItemId(e.target.value)}
      className="border rounded p-2 w-full"
    />
    <button
      type="button"
      onClick={handleFetchItem}
      className="bg-blue-600 text-white rounded mt-2 p-2"
    >
      Fetch Item
    </button>
    {editItem && (
      <div className="mt-4">
        <label htmlFor="editName" className="block mb-2">
          Name:
        </label>
        <input
          type="text"
          id="editName"
          value={editName}
          onChange={(e) => setEditName(e.target.value)}
          className="border rounded p-2 w-full"
        />
        <label htmlFor="editDescription" className="block mb-2 mt-4">
          Description:
        </label>
        <textarea
          id="editDescription"
          value={editDescription}
          onChange={(e) => setEditDescription(e.target.value)}
          className="border rounded p-2 w-full"
        />
        <button
          type="button"
          onClick={handleUpdateItem}
          className="bg-green-600 text-white rounded mt-2 p-2"
        >
          Update Item
        </button>
      </div>
    )}
  </div>
)}

// Remove Item
{tab === "remove" && (
  <div className="mt-4">
    <label htmlFor="removeItemId" className="block mb-2">
      Item ID:
    </label>
    <input
      type="text"
      id="removeItemId"
      value={removeItemId}
      onChange={(e) => setRemoveItemId(e.target.value)}
      className="border rounded p-2 w-full"
    />
    <button
      type="button"
      onClick={handleRemoveItem}
      className="bg-red-600 text-white rounded mt-2 p-2"
    >
      Remove Item
    </button>
  </div>
)}
<p>not done yet</p>
</div>
)}
</div>
</div>
);
};

export default AddEditItems;
