import React, { useRef, useState } from "react";
import { useDrop } from "react-dnd";

const ImageDropZone = () => {
  const [droppedImage, setDroppedImage] = useState(null);

  const [{ isOver }, drop] = useDrop(() => ({
    accept: "image",
    drop: (item) => setDroppedImage(item),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  const fileInputRef = useRef();

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setDroppedImage({ src: e.target.result, alt: file.name });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <button
        ref={drop}
        onClick={handleButtonClick}
        style={{
          padding: "10px 20px",
          backgroundColor: isOver ? "lightgray" : "whitesmoke",
          border: "1px dashed gray",
          cursor: "pointer",
        }}
      >
        {droppedImage ? "Image uploaded" : "Click or drag an image here"}
      </button>
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        style={{ display: "none" }}
      />
      {droppedImage && (
        <img
          src={droppedImage.src}
          alt={droppedImage.alt}
          style={{ maxWidth: "200px", maxHeight: "200px", marginTop: "10px" }}
        />
      )}
    </>
  );
};

export default ImageDropZone;
