import React, { useState, useEffect } from "react";
import { Route, Link, Routes } from "react-router-dom";
import SelectedPizzas from "./SelectedPizzas";
import AddRemovePizzas from "./AddRemovePizzas";
import "./Admin.css";
import { useRef } from "react";
import { io } from "socket.io-client";
import LoginForm from "./LoginForm";


const ADMIN_USERNAME = "dubz";
const ADMIN_PASSWORD = "130478";

export default function Admin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState(null);
  const [selectedPizzas, setSelectedPizzas] = useState([]);
  const [pizzaOptions, setPizzaOptions] = useState([]);
  const [loading, setLoading] = useState(true);

  const [newItemName, setNewItemName] = useState("");
  const [newItemImage, setNewItemImage] = useState("");
  const [newItemDescription, setNewItemDescription] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (username === ADMIN_USERNAME && password === ADMIN_PASSWORD) {
      setLoggedIn(true);
      setError(null);
    } else {
      setError("Invalid username or password");
    }
  };

  useEffect(() => {
    if (loggedIn) {
      const fetchData = async () => {
        setLoading(true);

        const [pizzaOptionsResponse, adminResponse] = await Promise.all([
          fetch("http://localhost:5000/pizza_options"),
          fetch("http://localhost:5000/admin"),
        ]);

        const pizzaOptionsData = await pizzaOptionsResponse.json();
        const adminData = await adminResponse.json();

        setPizzaOptions(pizzaOptionsData);
        setSelectedPizzas(adminData);
        setLoading(false);
      };

      fetchData();
    }
  }, [loggedIn]);

  const socketRef = useRef();

  useEffect(() => {
    if (loggedIn) {
      socketRef.current = io("http://localhost:5000");

      socketRef.current.on("connect", () => {
        console.log("Connected to server");
        socketRef.current.emit("admin_connected");
      });

      socketRef.current.on("selected_pizzas_update", (data) => {
        console.log("Received data:", data);
        if (data.length > 0) {
          const updatedData = data.map((newItem) => {
            const existingItem = selectedPizzas.find((item) => item.id === newItem.id);
            return existingItem ? { ...existingItem, count: newItem.count } : newItem;
          });
          setSelectedPizzas(updatedData);
        }
      });

      return () => {
        socketRef.current.disconnect();
      };
    }
  }, [loggedIn, selectedPizzas]);

  const getPizzaName = (id) => {
    const pizza = pizzaOptions.find((option) => option.id === parseInt(id));
    return pizza ? pizza.name : "Unknown";
  };

  const handleAddItem = async (event) => {
    event.preventDefault();

    const newItem = {
      name: newItemName,
      image: newItemImage,
      description: newItemDescription,
    };

    const response = await fetch("http://localhost:5000/add_item", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newItem),
    });

    const data = await response.json();

    if (data.status === "success") {
      setNewItemName("");
      setNewItemImage("");
      setNewItemDescription("");
      setPizzaOptions([...pizzaOptions, data.item]);
    } else {
      console.error(data.message);
    }
  };

  return (
    <div>
      {!loggedIn ? (
        <div className="container">
          <LoginForm
            username={username}
            password={password}
            setUsername={setUsername}
            setPassword={setPassword}
            handleSubmit={handleSubmit}
            error={error}
          />
        </div>
      ) : (
        <div className="admin-container">
          <div className="sidebar">
            <ul>
              <li>
                <Link to="/admin/selected-pizzas">Selected Pizzas</Link>
              </li>
              <li>
                <Link to="/admin/add-remove-pizzas">Add/Remove Pizzas</Link>
              </li>
            </ul>
          </div>
          <div className="admin-content">
            <Routes>
              <Route path="/admin/selected-pizzas" element={<SelectedPizzas />} />
              <Route path="/admin/add-remove-pizzas" element={<AddRemovePizzas handleAddItem={handleAddItem} />} />
            </Routes>
          </div>
        </div>
      )}
    </div>
  );
}
