import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const ADMIN_USERNAME = "dubz";
const ADMIN_PASSWORD = "130478";

export default function Admin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState(null);
  const [selectedPizzas, setSelectedPizzas] = useState([]);
  const [pizzaOptions, setPizzaOptions] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (username === ADMIN_USERNAME && password === ADMIN_PASSWORD) {
      setLoggedIn(true);
      setError(null);
    } else {
      setError("Invalid username or password");
    }
  };

  useEffect(() => {
    if (loggedIn) {
      const fetchData = async () => {
        setLoading(true);

        const [pizzaOptionsResponse, adminResponse] = await Promise.all([
          fetch("http://localhost:5000/pizza_options"),
          fetch("http://localhost:5000/admin"),
        ]);

        const pizzaOptionsData = await pizzaOptionsResponse.json();
        const adminData = await adminResponse.json();

        setPizzaOptions(pizzaOptionsData);
        setSelectedPizzas(adminData);
        setLoading(false);
      };

      fetchData();

      const socket = io("http://localhost:5000");

      socket.on("selected_pizzas_update", (data) => {
        console.log("Received data:", data);
        if (data.length > 0) {
          const mergedData = [...selectedPizzas, ...data];
          const uniqueData = Array.from(
            new Map(mergedData.map((item) => [item.id, item])).values()
          );
          setSelectedPizzas(uniqueData);
        }
      });

      return () => {
        socket.disconnect();
      };
    }
  }, [loggedIn]);

  const getPizzaName = (id) => {
    const pizza = pizzaOptions.find((option) => option.id === parseInt(id));
    return pizza ? pizza.name : "Unknown";
  };

  return (
    <div>
      {!loggedIn ? (
        <form onSubmit={handleSubmit}>
          <label>
            Username:
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <br />
          <button type="submit">Log in</button>
          {error && <p>{error}</p>}
        </form>
      ) : (
        <div>
          <h1>Selected Pizzas</h1>
          {loading ? (
            <p>Loading...</p>
          ) : (
            selectedPizzas && (
              <ul>
                {selectedPizzas.map((pizza, index) => (
                  <li key={index}>
                    {getPizzaName(pizza.id)} - {pizza.count} votes
                  </li>
                ))}
              </ul>
            )
          )}
        </div>
      )}
    </div>
  );
}
