import React, { useState, useEffect } from "react";
import axios from "axios";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import "react-tabs/style/react-tabs.css";

const AddEditItems = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [tab, setTab] = useState("add");
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [editName, setEditName] = useState("");
  const [editDescription, setEditDescription] = useState("");

  useEffect(() => {
    const fetchItems = async () => {
      const response = await axios.get("http://localhost:5000/items");
      setItems(response.data);
    };
    fetchItems();
  }, []);

  const onFileChange = (e) => setImage(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !description || !image) {
      alert("Please fill all the fields and select an image.");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", image);

    try {
      const response = await axios.post("http://localhost:5000/add_item", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (response.data.status === "success") {
        alert("Item added successfully!");
        setName("");
        setDescription("");
        setImage(null);
      } else {
        alert("Error: " + response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error: Unable to add the item.");
    }
  };

  const handleUpdateItem = async () => {
    if (!editName || !editDescription) {
      alert("Please fill all the fields.");
      return;
    }

    try {
      const response = await axios.put(`http://localhost:5000/update_item/${selectedItem}`, {
        name: editName,
        description: editDescription,
      });

      if (response.data.status === "success") {
        alert("Item updated successfully!");
        setEditName("");
        setEditDescription("");
        setSelectedItem(null);
      } else {
        alert("Error: " + response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error: Unable to update the item.");
    }
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-center text-3xl my-4">Add/Edit Items</h1>
      <Tabs selectedIndex={tab === "add" ? 0 : 1} onSelect={(index) => setTab(index === 0 ? "add" : "edit")}>
        <TabList>
          <Tab>Add</Tab>
          <Tab>Edit</Tab>
        </TabList>

        <TabPanel>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block mb-1">
                Name:
              </label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="border-2 border-gray-300 w-full py-2 px-4 rounded"
              />
            </div>
            <div>
              <label htmlFor="description" className="block mb-1">
                Description:
              </label>
              <textarea
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="border-2 border-gray-300 w-full py-2 px-4 rounded"
              />
            </div>
            <div>
              <label htmlFor="image" className="block mb-1">
                Image:
              </label>
              <input
                type="file"
                id="image"
                accept="image/*"
                onChange={onFileChange}
                className="border-2 border-gray-300 py-2 px-4 rounded"
              />
            </div>
            <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded">
              Submit
            </button>
          </form>
        </TabPanel>
        <TabPanel>
          <div className="mt-4">
            <label htmlFor="item" className="block mb-1">
              Select an item to edit:
            </label>
            <select
              id="item"
              className="border-2 border-gray-300 w-full py-2 px-4 rounded"
              value={selectedItem || ""}
              onChange={(e) => setSelectedItem(e.target.value)}
            >
              <option value="">Select an item</option>
              {items.map((item) => (
                <option key={item._id} value={item._id}>
                  {item.name}
                </option>
              ))}
            </select>
            {selectedItem && (
              <div className="mt-4">
                <label htmlFor="editName" className="block mb-1">
                  Edit Name:
                </label>
                <input
                  type="text"
                  id="editName"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  className="border-2 border-gray-300 w-full py-2 px-4 rounded"
                />
                <label htmlFor="editDescription" className="block mb-1 mt-4">
                  Edit Description:
                </label>
                <textarea
                  id="editDescription"
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                  className="border-2 border-gray-300 w-full py-2 px-4 rounded"
                />
                <button
                  type="button"
                  className="bg-blue-500 text-white py-2 px-4 rounded mt-4"
                  onClick={handleUpdateItem}
                >
                  Update
                </button>
              </div>
            )}
          </div>
        </TabPanel>
      </Tabs>
    </div>
  );
};

export default AddEditItems;
