import React, { useState, useEffect } from "react";
import "./App.css";

export default function App() {
  const [pizzaOptions, setPizzaOptions] = useState([]);
  const [selectedPizzas, setSelectedPizzas] = useState(new Set());

  useEffect(() => {
    fetch("/pizza_options")
      .then((res) => res.json())
      .then((data) => setPizzaOptions(data));
  }, []);

  const handleChange = (pizzaId) => {
    if (selectedPizzas.has(pizzaId)) {
      selectedPizzas.delete(pizzaId);
    } else {
      selectedPizzas.add(pizzaId);
    }
    setSelectedPizzas(new Set(selectedPizzas));
  };

  const handleSubmit = () => {
    fetch("/selected_pizzas", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(Array.from(selectedPizzas)),
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  return (
    <div className="App">
      <div className="overlay"></div>
      <div className="pizza-container">
        {pizzaOptions.map((pizza, i) => (
          <div
            key={i}
            className={`pizza-box ${selectedPizzas.has(pizza.id) ? "selected" : ""}`}
            onClick={() => handleChange(pizza.id)}
          >
            <img src={pizza.image} alt={pizza.name} />
            <div class = "pizza">{pizza.name}</div>
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}
