import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const ADMIN_USERNAME = "dubz";
const ADMIN_PASSWORD = "130478";

export default function Admin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [error, setError] = useState(null);
  const [selectedPizzas, setSelectedPizzas] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (username === ADMIN_USERNAME && password === ADMIN_PASSWORD) {
      setLoggedIn(true);
      setError(null);
    } else {
      setError("Invalid username or password");
    }
  };

  useEffect(() => {
    if (loggedIn) {
      const socket = io();

      socket.on("selected_pizzas_update", (data) => {
        setSelectedPizzas(data);
      });

      return () => {
        socket.disconnect();
      };
    }
  }, [loggedIn]);

  return (
    <div>
      {!loggedIn ? (
        <form onSubmit={handleSubmit}>
          <label>
            Username:
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <br />
          <button type="submit">Log in</button>
          {error && <p>{error}</p>}
        </form>
      ) : (
        <div>
          <h1>Selected Pizzas</h1>
          {selectedPizzas && (
            <ul>
              {selectedPizzas.map((pizza, index) => (
                <li key={index}>{JSON.stringify(pizza)}</li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
