import React, { useState } from "react";
import "./AddEditItems.css";

function AddEditItems() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [selectedImage, setSelectedImage] = useState(null);

  const onImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const onDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle the form submission here
    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("image", selectedImage);
  
    const response = await fetch("http://localhost:5000/api/add_item", {
      method: "POST",
      headers: {
        "Content-Type": "multipart/form-data",
      },
      body: formData,
    });
  
    if (response.ok) {
      const data = await response.json();
      console.log("Item added successfully:", data);
      // Reset the form fields and selected image
      setName("");
      setDescription("");
      setSelectedImage(null);
    } else {
      console.error("Error adding item:", await response.json());
    }
  };
  

  return (
    <DndProvider backend={HTML5Backend}>
      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div>
            <h2>Images</h2>
            <ImageItem src={selectedImage} alt="Selected Image" />
            {/* Add more ImageItem components for each image */}
          </div>
          <ImageDropZone />
        </div>
        <div>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            style={{ marginBottom: "1rem" }}
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Item Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="text"
            placeholder="Item Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button
          type="submit"
          disabled={!selectedImage || name.trim() === "" || description.trim() === ""}
        >
          Submit
        </button>
      </form>
    </DndProvider>
  );
};

export default AddEditItems;
